import React, {useState } from 'react'
import {useDispatch, useSelector} from 'react-redux'
import FormContainer from '../components/formContainer'
// import CheckoutSteps from '../components/CheckoutSteps'
import {savePaymentMethod} from '../actions/cartActions' 

const PaymentMethodScreen = ({ history }) => {
    const cart = useSelector(state => state.cart)
    const {shippingAddress} = cart

    if(!shippingAddress){
        history.push('/shipping')
    }

    const [paymentMethod, setPaymentMethod] = useState('PayPal')


    const dispatch = useDispatch()

    const submitHandler = (e) => {
        e.preventDefault()
        dispatch(savePaymentMethod(paymentMethod))
        history.push('/placeorder')
    }

    return (
        <FormContainer>
            {/* <CheckoutSteps step1 step2 step3></CheckoutSteps> */}
            <h1>SHIPPING</h1>
            <form onSubmit={submitHandler}>
                <div className="form-group" id="address">
                    <label className="form-label">Select Method</label>
                </div>
                <div className="col">
                    <div className="form-check" type="radio" label="PayPal or Credit Card" id="PayPal" name="paymentMethod" value="PayPal" checked onChange={(e) => setPaymentMethod(e.target.value)}>

                    </div>
                    <div class="form-check">
                        <input class="form-check-input" type="radio" label="PayPal or Credit Card" name="paymentMethod" id="PayPal"/>

                    </div>
                </div>
                <div className="d-grid gap-2 py-2">
                    <button className="btn btn-dark" type="submit">Continue</button>
                </div>
            </form>
        </FormContainer>
    )
}

export default PaymentMethodScreen
