import React from 'react'
import {useDispatch, useSelector} from 'react-redux'
import {Link} from 'react-router-dom'
import {logout} from '../actions/userActions'

const Header = () => {

    const dispatch = useDispatch()
    const userLogin = useSelector(state => state.userLogin)
    const {userInfo} = userLogin

    const logoutHandler = () => {
        dispatch(logout())
    }

    return (
        <header>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container">
                    <Link className="navbar-brand mr-auto" to="/">E-Commerce</Link>
                    <div className="d-flex" id="navbarSupportedContent">

                        <ul className="navbar-nav me-2">
                            <li className="nav-item">
                                <Link className="nav-link" to="/cart"><i className="fas fa-shopping-cart"></i> Cart</Link>
                            </li>
                            <li className="nav-item">
                                {userInfo ? (
                                    // <div className="nav-item dropdown">
                                    //     <Link className="nav-link dropdown-toggle" to='#' id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                                    //     {userInfo.name}
                                    //     </Link>
                                    //     <div className="dropdown-item" onClick={logoutHandler}>Logout</div>
                                    //     <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                                    //         <li className="dropdown-item"><Link to='/profile'>Profile</Link></li>    
                                    //     </ul>
                                    // </div>
                                    <li class="dropdown nav-item">
                                        <L class="dropbtn">{userInfo.name}</L>
                                        <div class="dropdown-content">
                                            <Link to="/profile">Profile</Link>
                                            <span onClick={logoutHandler}>Logout</span>
                                        </div>
                                    </li>

                                ) : 
                                <Link className="nav-link" to="/login"><i className="fas fa-user"></i> Sign In</Link>}
                            </li>
                        </ul>

                    </div>
                </div>
            </nav>
        </header>
    )
}

export default Header
