import React from 'react'
import {LinkContainer} from 'react-router-bootstrap'

const CheckoutSteps = ({step1,step2,step3,step4}) => {
    return (
        <nav className='justify-content-center mb-4'>
            <ul className='navbar-nav'>
                <li className='nav-item'>
                    {step1 ? (
                    <LinkContainer to='/login'>

                        <span className='nav-link'>Sign In</span>
                    </LinkContainer>
                    ) : (
                    <span className='nav-link' disabled>Sign In</span>
                    )}
                </li>
                <li className='nav-item'>
                    {step2 ? (
                    <LinkContainer to='/shipping'>
                        <span className='nav-link'>Shipping</span>
                    </LinkContainer>
                    ) : (
                    <span className='nav-link' disabled>Shipping</span>
                    )}
                </li>
                <li className='nav-item'>
                    {step3 ? (
                    <LinkContainer to='/payment'>
                        <span className='nav-link'>Payment</span>
                    </LinkContainer>
                    ) : (
                    <span className='nav-link' disabled>Payment</span>
                    )}
                </li>
                <li className='nav-item'>
                    {step4 ? (
                    <LinkContainer to='/placeorder'>
                        <span className='nav-link'>Place Order</span>
                    </LinkContainer>
                    ) : (
                    <span className='nav-link' disabled>Place Order</span>
                    )}
                </li>
            </ul>
        </nav>
    )
}

export default CheckoutSteps
